// frontend/src/ProfileMenu.js - UPDATED WITH PROFILE IMAGE

import React, { useState } from 'react';

// This component manages the dropdown and sends the command to open the modal
function ProfileMenu({ username, handleLogout, setIsProfileOpen, isAdmin, openAdminTool }) {
  const [isOpen, setIsOpen] = useState(false);

  // Function to open the modal and set the starting tab
  const openModal = (tabName) => {
    // Passes object: { isOpen: true, activeTab: 'profile' or 'settings' }
    setIsProfileOpen({ isOpen: true, activeTab: tabName });
    setIsOpen(false); // Close the dropdown menu
  };

  return (
    <div className="profile-container">
      {/* Profile Icon with Image - Clicking toggles the dropdown */}
      <div className="profile-icon" onClick={() => setIsOpen(!isOpen)}>
        <img 
          src="/profile-icon.png" 
          alt="Profile" 
          onError={(e) => {
            // Fallback: If image fails to load, show first letter of username
            e.target.style.display = 'none';
            e.target.parentElement.innerHTML = <span style="font-size: 1.2rem; font-weight: 700; color: #000;">${username.charAt(0).toUpperCase()}</span>;
          }}
        />
      </div>

      {/* Profile Dropdown Menu */}
      {isOpen && (
        <div className="profile-dropdown">
          <p className="dropdown-username">{username}</p>
          <hr />
          
          {/* Link to open the 'Your Profile' tab */}
          <div className="dropdown-item" onClick={() => openModal('profile')}>
            Your Profile
          </div>
          
          {/* Link to open the 'Settings' tab */}
          <div className="dropdown-item" onClick={() => openModal('settings')}>
            Settings
          </div>
          
          {/* ADMIN LINK - Only visible to admins */}
          {isAdmin && (
            <>
              <hr />
              <div 
                className="dropdown-item admin-action" 
                onClick={() => {
                  openAdminTool('user_bookings');
                  setIsOpen(false); // Close dropdown after clicking
                }}
              >
                User Bookings
              </div>
            </>
          )}
          
          <hr />
          
          {/* Logout Button */}
          <div className="dropdown-item logout" onClick={handleLogout}>
            Logout
          </div>
        </div>
      )}
    </div>
  );
}

export default ProfileMenu;
