// frontend/src/Dashboard.js - COMPLETE CODE WITH FIXED ADMIN DETECTION

import React, { useState, useEffect } from 'react';
import { Routes, Route, Link } from 'react-router-dom';
import ProfileMenu from './ProfileMenu';
import ProfileSettings from './ProfileSettings';
import AdminToolModal from './AdminToolModal';

import HomePage from './HomePage';
import PlacesPage from './PlacesPage';
import BookingConfirmationPage from './BookingConfirmationPage';
import ContactPage from './ContactPage';
import PackagesPage from './PackagesPage';

function Dashboard({ username, setIsLoggedIn, setUsername }) {
  // isProfileOpen MUST be an object to manage modal visibility AND active tab
  const [isProfileOpen, setIsProfileOpen] = useState({ isOpen: false, activeTab: 'profile' });
  
  // NEW STATE: Store admin status
  const [isAdmin, setIsAdmin] = useState(false);
  
  // NEW STATE: Tool Modal
  const [isToolModalOpen, setIsToolModalOpen] = useState(false);
  const [activeTool, setActiveTool] = useState(null); // 'add_place' or 'user_bookings'

  // NEW useEffect to check admin status
  useEffect(() => {
    const checkAdmin = async () => {
      try {
        const response = await fetch('/api/status');
        if (response.ok) {
          const data = await response.json();
          console.log('Admin status check:', data); // Debug log
          if (data.isAdmin) {
            setIsAdmin(true);
            console.log('✅ Admin access granted');
          } else {
            setIsAdmin(false);
            console.log('ℹ Regular user access');
          }
        }
      } catch (err) {
        console.error("Error checking admin status:", err);
      }
    };
    checkAdmin();
  }, [username]); // Re-check when username changes

  const handleLogout = () => {
    fetch('/api/logout', { method: 'POST' })
      .then(() => {
        setIsLoggedIn(false);
        setUsername(null);
        setIsAdmin(false); // Reset admin status on logout
      })
      .catch(err => console.error("Logout failed:", err));
  };

  const openAdminTool = (toolName) => {
    setActiveTool(toolName);
    setIsToolModalOpen(true);
  };

  return (
    <div className="dashboard-page">
      <header className="dashboard-header">
        <div className="logo">TMS Dashboard</div>
        
        <nav className="main-nav">
          <Link to="/" className="nav-link">Home</Link>
          <Link to="/places" className="nav-link">Places</Link>
          <Link to="/packages" className="nav-link">Packages</Link>
          <Link to="/contact" className="nav-link">Contact</Link>
        </nav>
        
        {/* ADMIN OPTION: Add Place Button in Header */}
        {isAdmin && (
          <button 
            onClick={() => openAdminTool('add_place')} 
            className="admin-header-btn"
            style={{
              backgroundColor: '#e74c3c',
              color: 'white',
              border: 'none',
              padding: '8px 16px',
              borderRadius: '5px',
              cursor: 'pointer',
              fontWeight: 'bold',
              marginRight: '25px'
            }}
          >
            + Add Place
          </button>
        )}
        
        <ProfileMenu
          username={username}
          handleLogout={handleLogout}
          setIsProfileOpen={setIsProfileOpen}
          isAdmin={isAdmin}
          openAdminTool={openAdminTool}
        />
      </header>

      <div className="main-app-body">
        <Routes>
          <Route path="/" element={<HomePage username={username} />} />
          <Route path="/places" element={<PlacesPage />} />
          <Route path="/contact" element={<ContactPage />} />
          <Route path="/packages" element={<PackagesPage />} />
          <Route path="/book/:placeName" element={<BookingConfirmationPage />} />
        </Routes>
      </div>
      
      {/* Profile Settings Modal */}
      {isProfileOpen.isOpen && (
        <ProfileSettings
          username={username}
          activeTab={isProfileOpen.activeTab}
          setIsProfileOpen={setIsProfileOpen}
        />
      )}

      {/* NEW: Admin Tool Modal (Create/Read) */}
      {isToolModalOpen && (
        <AdminToolModal
          activeTool={activeTool}
          onClose={() => setIsToolModalOpen(false)}
        />
      )}
    </div>
  );
}

export default Dashboard;
